//   html2canvas(document.querySelector("#invoiceCapture")).then((canvas) => {
//     const imgData = canvas.toDataURL("image/png", 1.0);
//     // const pdf = new jsPDF({
//     //   orientation: "portrait",
//     //   unit: "pt",
//     //   format: [612, 792],
//     // });
